import { createStore } from 'redux'; // step-1 i import createStore from redux

const initialState = { counter: 0, showCounter: true }

const counterReducer = (state = initialState, action) => { // step-3 Create Reducer
    if (action.type === 'inc') {
        return {
            counter: state.counter + 1,
            showCounter: state.showCounter
        }
    }

    if (action.type === 'icr5') {
        return {
            counter: state.counter + action.high,
            showCounter: state.showCounter
        }
    }


    if (action.type === 'dcr') {
        return {
            counter: state.counter - 1,
            showCounter: state.showCounter
        }
    }

    if (action.type === 'toggle') {
        return {
            showCounter: !state.showCounter,
        }
    }

    return state
};

const store = createStore( // Step-1  Create a Store 
    counterReducer // Step-3  point the reducer in Store
);
/* const store = createStore(counterReducer) // step -2 assin createStore to identify store, //  step-4 keep reducer into store
 */
export default store; // step-5 export 1 store  // nxt to index.js to provide data from store
