import { useSelector, useDispatch } from 'react-redux'; // step-10 import selector to use data to access data
import classes from './Counter.module.css';
import { counterActions } from '../store/storeindex';

const Counter = () => {
  const dispatch = useDispatch()  // step-12 useDispatch to dispatch action
  const counter = useSelector(state => state.counter);  // step-11 use useSelector(), pass fun as para to get data
  const show = useSelector(state => state.showCounter)

  const incHandler = () => {
    dispatch(counterA) // step-13 use same identifiers name inside dispatch
  }

  const inc5Handler = () => {
    dispatch({ type: 'icr5', amount: 5 })
  }

  const dcrHandler = () => {
    dispatch({ type: 'dcr' })
  }

  const toggleCounterHandler = () => {
    dispatch({ type: "toggle" })
  };

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      {show && <div className={classes.value}>{counter}</div>}
      <div>
        <button onClick={incHandler}>INC</button>
        <button onClick={dcrHandler}>DCR</button>
        <button onClick={inc5Handler}>ICR by 5</button>
      </div>
      <button onClick={toggleCounterHandler}>Toggle Counter</button>
    </main>
  );
};

export default Counter;
