import React from 'react';
import ReactDOM from 'react-dom/client';
import { Provider } from 'react-redux'; // step-6 import provider from Redux

import './index.css';
import App from './App';
import store from './store/index'; // step-8  import store path

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<Provider store={store}><App /></Provider>);  // step-7 keep APP inside Provider to access data to all components
