// import { createStore } from 'redux'; // step-1 i import createStore from redux
import { createSlice } from '@reduxjs/toolkit';
import { configureStore } from '@reduxjs/toolkit';

// initial State for counter
const initialCounterState = { counter: 0, showCounter: true }

// createSlice for Counter
const counterSlice = createSlice({
    name: 'counter',
    initialCounterState,
    reducers: {
        inc(state) {
            state.counter++;
        },

        dcr(state) {
            state.counter--;
        },

        inc5(state, action) {
            state.counter = state.counter + action.payload
        },

        toggle(state) {
            state.showCounter = !state.showCounter
        }
    }
});

// initial state for authentication
const initialAuthState = {
    isAuthenticated: false
}

const authSlice = createSlice({
    name: 'authentication',
    initialAuthState,
    reducers: {
        login(state) {
            state.isAuthenticated = true;
        },
        logout(state) {
            state.isAuthenticated = false;
        }
    }
})





const store = configureStore({
    reducer: counterSlice.reducer
}) // step -2 assin createStore to identify store, //  step-4 keep reducer into store

export const counterActions = counterSlice.actions;
export default store; // step-5 export 1 store  // nxt to index.js to provide data from store







