import { createStore } from 'react-redux' // step-1 i react-redux and import createStore 

const counterReducer = (state = { counter: 0 }, action) => { // step-3 Create Reducer
    if (action.type === 'inc') {
        return {
            counter: state.counter + 1
        }
    }

    if (action.type === 'dcr') {
        return {
            counter: state.counter - 1
        }
    }

    return state
};

const store = createStore(
    counterReducer
    ) // step -2 assin createStore to identify store, //  step-4 keep reducer into store

export default store; // step-5 export 1 store  // nxt to index.js to provide data from store
